import React, { useState, useEffect } from 'react'
import './App.css'

function App() {
  const [slideNo, setSlideNo] = useState(1)
  const src = `./slides/${slideNo}.png`

  useEffect(() => {
    document.onkeydown = e => {
      console.log(e)

      e = e || window.event;

      switch (e.keyCode) {
        case '38': // up arrow
        case '37': // left arrow
          setSlideNo(slideNo + 1)
          break
        case '39': // right arrow
        case '40': // down arrow
          setSlideNo(slideNo - 1)
          break
        default:
          break
      }
    }

    return () => { }
  }, [slideNo])


  return (
    <div>
      <img
        src={src}
        style={{ width: '100%' }}
        alt='' />
    </div>
  )
}

export default App;
