import React, { useState, useEffect } from 'react'
import logo from './logo.svg'
import './App.css'

function App() {
  const [slideNo, setSlideNo] = useState(1)
  useEffect(() => {
    document.onkeydown = e => {

      e = e || window.event;

      switch (e.keyCode) {
        case '38': // up arrow
        case '37': // left arrow

          break
        case '39': // right arrow
        case '40': // down arrow

          break
      }

    }
  }, [])
  return (
    <div>
      <img
        src={`./slides/.png${slideNo}`}
        style={{ width: '100%' }} />
    </div>
  )
}

export default App;
