import React, { useState, useEffect } from 'react'
import './App.css'

function App() {
  const [slideNo, setSlideNo] = useState(1)
  const src = `./slides/${slideNo}.png`

  useEffect(() => {
    document.onkeydown = e => {
      console.log(e)

      e = e || window.event;
      let current = slideNo

      switch (e.keyCode) {
        case 38: // up arrow
        case 37: // left arrow
          current++
          break
        case 39: // right arrow
        case 40: // down arrow
          current--
          break
        default:
          console.log({ keyCode: e.keyCode })
          break
      }

      console.log({ current })
      setSlideNo(current)
    }

    return () => { }
  })

  console.log({ src })
  return (
    <div>
      <img
        src={src}
        style={{ width: '100%' }}
        alt='' />
    </div>
  )
}

export default App;
