import React, { useState, useEffect } from 'react'
import client from './client'
import './App.css'

export default function App() {
	const [password, setPassword] = useState('')
	const [slideIndex, setSlideIndex] = useState(0)
	const [slides, setSlides] = useState([]) // slide names
	const [isPresenter, setIsPresenter] = useState(false)
	const DO_PRESENT = getUrlParam('present').toLowerCase() === 'yes'
	const handleLogin = deferred(e => {
		if (!client.isConnected()) return
		e && e.preventDefault()
		client.login(password, (_, admin, slides, index) => {
			password && setIsPresenter(admin)
			setSlides(slides || [])
			setSlideIndex(index)
			console.log({ slides, index, admin })
		})
	}, 100)

	useEffect(() => {
		handleLogin()
		client.onSlideIndex((index) => {
			setSlideIndex(index)
			console.log('Slide changed', index)
		})

		client.onConnect(() => {
			if (!client.isConnected()) return
			isPresenter && password && handleLogin()
			slides.length === 0 && client.getSlides(slides => setSlides(slides))
		})
		const handleKeyPress = e => {
			if (!isPresenter) return
			// control slide on arrow key press and page up/down key press
			e = e || window.event;
			let index = slideIndex
			switch (e.keyCode) {
				case 35: // End key
					index = slides.length - 1 // go to last slide
					break
				case 36: // Home key
					index = 0 // go to first slide
					break
				case 38: // up arrow
				case 37: // left arrow
					index = slideIndex - 1 // go to previous slide
					break
				case 39: // right arrow
				case 40: // down arrow
					index = slideIndex + 1 // go to next slide
					break
				default:
					// console.log({ keyCode: e.keyCode })
					break
			}
			if (index < 0 || index >= slides.length) return
			client.setCurrentSlide(index, (err) => {
				console.log('setCurrentSlide', { err })
			})
		}
		document.onkeydown = handleKeyPress
		return () => { }
	}, [slides, slideIndex, isPresenter])

	if (DO_PRESENT && !isPresenter) return (
		<div style={{ textAlign: 'center' }}>
			<h3>Please enter password to login</h3>
			<form onSubmit={handleLogin}>
				<input
					type='password'
					onChange={e => setPassword(e.target.value)}
					placeholder='Enter password'
					style={styles.input}
				/>
			</form>
			<div>
				<button
					style={styles.button}
					onClick={handleLogin}
				>
					Login as presenter
				</button>
				<button
					style={styles.button}
					onClick={() => window.location.href = '?present=no'}
				>
					View Only
				</button>
			</div>
		</div>
	)
	return (
		<div>
			<div style={styles.counter}>
				{slideIndex + 1}/{slides.length}
			</div>
			<div style={styles.imgContainer}>
				{(slides || []).map((slide, index) => (
					<img {...{
						src: `./slides/${slide}`,
						style: {
							display: slideIndex !== index ? 'none' : 'block',
							width: '100%',
						},
						alt: slide,
					}} />
				))}
			</div>
		</div>
	)
}

const styles = {
	button: {
		background: '#dadada',
		cursor: 'pointer',
		fontWeight: 'bold',
		padding: '10px 15px',
	},
	counter: {
		position: 'fixed',
		bottom: 25,
		left: 25,
		fontWeight: 'bold',
		color: 'grey',
	},
	imgContainer: {
		height: '100%',
		width: '100%',
		overflow: 'hidden',
	},
	input: {
		padding: '10px 15px',
		borderRadius: 3,
		border: '1px solid grey'
	},
}

// getUrlParam reads the URL parameters
//
// Params:
// @name    string: (optional) if supplied will return a specific paramenter as string.
//                  Otherwise, will return an object containing all the URL parameters with respective values.
//
// returns  string/object
function getUrlParam(name) {
	const params = {}
	const regex = /[?&]+([^=&]+)=([^&]*)/gi
	window.location.href.replace(regex, (_, key, value) => params[key] = value)
	return name ? params[name] || '' : params
}

// deferred returns a function that invokes the callback function after certain delay/timeout
// If the returned function is invoked again before timeout,
// the invokation will be deferred further with the duration supplied in @delay
//
// Params:
// @callback  function  : function to be invoked after deferred delay
// @delay     number    : number of milliseconds to be delayed.
//                        Default value: 50
// @thisArg    object   : optional, makes sure callback is bounded to supplied object 
export function deferred(callback, delay, thisArg) {
	let timeoutId
	return function () {
		const args = arguments
		if (timeoutId) clearTimeout(timeoutId)
		timeoutId = setTimeout(function () {
			callback && callback.apply(thisArg, args)
		}, delay || 50)
	}
}
