import React, { useState, useEffect } from 'react'
import './App.css'
window.numSlides = prompt('Number of slides')
console.log(window.numSlides)
function App() {
  const [slideNo, setSlideNo] = useState(1)
  const src = `./slides/${slideNo}.png`
  useEffect(() => {
    document.onkeydown = e => {
      e = e || window.event;
      let next = slideNo

      switch (e.keyCode) {
        case 35: // End key
          break
        case 36: // Home key
          next = 1
          break
        case 38: // up arrow
        case 37: // left arrow
          next = slideNo - 1
          break
        case 39: // right arrow
        case 40: // down arrow
          next = slideNo + 1
          break
        default:
          // console.log({ keyCode: e.keyCode })
          break
      }
      next > 0 && setSlideNo(next)
    }

    return () => { }
  }, [slideNo])

  console.log({ src })
  return (
    <div>
      <img {...{
        src: src,
        style: { width: '100%' },
        alt: '',
      }}
      />
    </div>
  )
}

export default App;
