import React, { useState, useEffect } from 'react'
import client from './client'
import './App.css'

const DO_PRESENT = getUrlParam('present').toLowerCase() === 'yes'
export default function App() {
	const [password, setPassword] = useState('')//localStorage.getItem('password')
	const [slideIndex, setSlideIndex] = useState(1)
	const [slides, setSlides] = useState([]) // slide names
	const [isPresenter, setIsPresenter] = useState(null)
	const handleKeyPress = e => {
		// control slide on arrow key press and page up/down key press
		e = e || window.event;
		let index = slideIndex
		console.log('key pressed', e.keyCode)
		switch (e.keyCode) {
			case 35: // End key
				// go to last slide
				break
			case 36: // Home key
				index = 0 // go to first slide
				break
			case 38: // up arrow
			case 37: // left arrow
				index = slideIndex - 1 // go to previous slide
				break
			case 39: // right arrow
			case 40: // down arrow
				index = slideIndex + 1 // go to next slide
				break
			default:
				// console.log({ keyCode: e.keyCode })
				break
		}
		if (index < 0 || index >= slides.length) return
		client.setCurrentSlide(index, () => { })
	}

	useEffect(() => {
		client.onSlideIndex(index => setSlideIndex(index) | console.log('Index changed', index))

		return () => { }
	}, [slideIndex])

	if (DO_PRESENT && isPresenter === null) {
		const handleLogin = () => client.login(password, (admin, slides) => {
			setSlides(slides)
			setIsPresenter(!!admin)
			console.log({ admin, slides })
			if (!admin) localStorage.setItem('password', password)
			if (!!document.onkeydown) return
			document.onkeydown = handleKeyPress
			// // auto relogin
			// client.reconnect(() => handleLogin())
		})
		if (!localStorage.getItem('password')) return (
			<div>
				<h3>Please enter password to login</h3>
				<form onSubmit={handleLogin}>
					<input type='password' onChange={e => setPassword(e.target.value)} />
				</form>

			</div>
		)
		handleLogin()
	}
	return (
		<div>
			{password}
			{slides}
			<img {...{
				src: `./slides/${slideIndex}.png`,
				style: { width: '100%' },
				alt: '',
			}}
			/>
		</div>
	)
}

// getUrlParam reads the URL parameters
//
// Params:
// @name    string: (optional) if supplied will return a specific paramenter as string.
//                  Otherwise, will return an object containing all the URL parameters with respective values.
//
// returns  string/object
function getUrlParam(name) {
	const params = {}
	const regex = /[?&]+([^=&]+)=([^&]*)/gi
	window.location.href.replace(regex, (_, key, value) => params[key] = value)
	return name ? params[name] || '' : params
}
