import React, { useState, useEffect } from 'react'
import client from './client'
import './App.css'

const DO_PRESENT = getUrlParam('present').toLowerCase() === 'yes'
export default function App() {
	const [password, setPassword] = useState(localStorage.getItem('password'))
	const [slideIndex, setSlideIndex] = useState(0)
	const [slides, setSlides] = useState([]) // slide names
	const [isPresenter, setIsPresenter] = useState(null)
	const handleLogin = e => {
		console.log('handleLogin')
		e && e.preventDefault()
		client.login(password, (admin, slides, index) => {
			setIsPresenter(!password ? null : admin)
			setSlides(slides || [])
			// setSlideIndex(index)
			if (password && !!admin) localStorage.setItem('password', password)
		})
	}
	useEffect(() => {
		handleLogin()
		client.onSlideIndex((index) => {
			setSlideIndex(index)
			console.log('Index changed', index)
		})

		client.onReconnect(() => isPresenter && password && handleLogin())

		// client.getSlides((_, slides) => setSlides(slides))

		const handleKeyPress = e => {
			if (!isPresenter) return
			// control slide on arrow key press and page up/down key press
			e = e || window.event;
			let index = slideIndex
			switch (e.keyCode) {
				case 35: // End key
					// go to last slide
					break
				case 36: // Home key
					index = 0 // go to first slide
					break
				case 38: // up arrow
				case 37: // left arrow
					index = slideIndex - 1 // go to previous slide
					break
				case 39: // right arrow
				case 40: // down arrow
					index = slideIndex + 1 // go to next slide
					break
				default:
					// console.log({ keyCode: e.keyCode })
					break
			}
			if (index < 0 || index >= slides.length) return
			client.setCurrentSlide(index, (err) => {
				// setSlideIndex(index)
				console.log('setSlide', { err })
			})
		}
		document.onkeydown = handleKeyPress
		return () => { }
	}, [slideIndex, isPresenter])

	if (DO_PRESENT && isPresenter === null) {
		if (!localStorage.getItem('password')) return (
			<div style={{ textAlign: 'center' }}>
				<h3>Please enter password to login</h3>
				<form onSubmit={handleLogin}>
					<input
						type='password'
						onChange={e => setPassword(e.target.value)}
						placeholder='Enter password'
						style={{
							padding: '10px 15px',
							borderRadius: 3,
							border: '1px solid grey'
						}}
					/>
					<div>
						<button
							style={styles.button}
							onClick={handleLogin}
						>
							Login as presenter
						</button>
						<button
							style={styles.button}
							onClick={e => e.preventDefault() | setIsPresenter(false)}
						>
							View Only
						</button>
					</div>
				</form>
			</div >
		)
		handleLogin()
	}
	return (
		<div>
			{(slides || []).map((slide, index) => (
				<img {...{
					src: `./slides/${slide}`,
					style: {
						display: slideIndex !== index ? 'none' : 'block',
						width: '100%',
					},
					alt: '',
				}} />
			))}
		</div>
	)
}

const styles = {
	button: {
		background: '#dadada',
		cursor: 'pointer',
		fontWeight: 'bold',
		padding: '10px 15px',
	}
}

// getUrlParam reads the URL parameters
//
// Params:
// @name    string: (optional) if supplied will return a specific paramenter as string.
//                  Otherwise, will return an object containing all the URL parameters with respective values.
//
// returns  string/object
function getUrlParam(name) {
	const params = {}
	const regex = /[?&]+([^=&]+)=([^&]*)/gi
	window.location.href.replace(regex, (_, key, value) => params[key] = value)
	return name ? params[name] || '' : params
}
